import React, { useEffect, useState } from "react";

import Board from "./Components/Board/Board";

import "./App.css";
import Editable from "./Components/Editabled/Editable";




function App() {

  const [data, setData] = useState(null);
  const [sortPriority, setSortPriority] = useState(false);

  const statusMap = {
    "Todo" : 0,
    "In progress" : 1,
    "Backlog":2,
    "Cancelled" : 4
  };

  useEffect(() => {
    // Fetch data from the API and store it in the 'data' state
    fetch('https://api.quicksell.co/v1/internal/frontend-assignment')
    .then((response) => response.json())
    .then((responseData) => handleData(responseData))
    .catch((error) => console.error('Error fetching data:', error));
  }, []);

  const user_map = {};
  const status_map = {};
  const priority_map = {};
  const [user_board, set_user_board] = useState([]);
  const [priority_board, set_priority_board] = useState([]);
  const [status_board, set_status_board] = useState([]);

  const handleData = (responseData) => {
    console.log(responseData);
    setData(responseData);
    {responseData.tickets.map((ticket)=>{
      if(ticket.userId in user_map){
        addCardss(user_board[ticket.userId], user_board, ticket,set_user_board);
      }
      else{
        const tempBoards = [...user_board];
        let idd = Date.now() + Math.random() * 2;
        tempBoards.push({
          id: idd,
          title: ticket.userId,
          cards: [],
        });
        set_user_board(tempBoards);
        user_map[ticket.userId]=idd;
        addCardss(user_map[ticket.userId], user_board, ticket, set_user_board);
        // console.log("aerear");

      }
      console.log(user_board);
      if(ticket.status in status_board){
        addCardss(status_board[ticket.status], status_board, ticket, set_status_board);
      }
      else{
        const tempBoards = [...status_board];
        tempBoards.push({
          id: Date.now() + Math.random() * 2,
          title: ticket.status,
          cards: [],
        });
        set_status_board(tempBoards);
        addCardss(status_board[ticket.status], status_board, ticket,set_status_board);

      }
      if(ticket.priority in priority_board){
        addCardss(priority_board[ticket.priority], priority_board, ticket, set_priority_board);
      }
      else{
        const tempBoards = [...priority_board];
        tempBoards.push({
          id: Date.now() + Math.random() * 2,
          title: ticket.priority,
          cards: [],
        });
        set_priority_board(tempBoards);
        addCardss(priority_board[ticket.priority], priority_board, ticket, set_priority_board);

      }

  })}

  }

  const addCardss = (id, board, card,set_board) => {
    console.log("red", id, board);
    const index = board.findIndex((item) => item.id === id);
    console.log(index);
    if (index < 0) return;
    const tempBoards = [...board];
    console.log("reached");
    // console.log(...card, "erere");
    tempBoards[index].cards.push({
      id: Date.now() + Math.random() * 2,
      ...card
    });
    set_board(tempBoards);
  };



  const [boards, setBoards] = useState(
    [
      {
        id:0,
        title:"Todo",
        cards:[],
      },
      {
        id:1,
        title:"In Progress",
        cards:[],
      },
      {
        id:2,
        title:"Done",
        cards:[],
      },
      {
        id:3,
        title:"Cancelled",
        cards:[],
      },
    ]
  );

  console.log(data);


  const [targetCard, setTargetCard] = useState({
    bid: "",
    cid: "",
  });

  const addboardHandler = (name) => {
    const tempBoards = [...boards];
    tempBoards.push({
      id: Date.now() + Math.random() * 2,
      title: name,
      cards: [],
    });
    setBoards(tempBoards);
  };

  const removeBoard = (id) => {
    const index = boards.findIndex((item) => item.id === id);
    if (index < 0) return;

    const tempBoards = [...boards];
    tempBoards.splice(index, 1);
    setBoards(tempBoards);
  };

  const addCardHandler = (id, titlee,descc, user, status ,tag,priority) => {
    const index = boards.findIndex((item) => item.id === id);
    if (index < 0) return;
    // console.log(descc);
    const tempBoards = [...boards];
    tempBoards[index].cards.push({
      id: Date.now() + Math.random() * 2,
      title : titlee,
      desc : descc,
      user,
      status,
      tag,
      priority
    });
    setBoards(tempBoards);
  };

  const removeCard = (bid, cid) => {
    const index = boards.findIndex((item) => item.id === bid);
    if (index < 0) return;

    const tempBoards = [...boards];
    const cards = tempBoards[index].cards;

    const cardIndex = cards.findIndex((item) => item.id === cid);
    if (cardIndex < 0) return;

    cards.splice(cardIndex, 1);
    setBoards(tempBoards);
  };

  const dragEnded = (bid, cid) => {
    let s_boardIndex, s_cardIndex, t_boardIndex, t_cardIndex;
    s_boardIndex = boards.findIndex((item) => item.id === bid);
    if (s_boardIndex < 0) return;

    s_cardIndex = boards[s_boardIndex]?.cards?.findIndex(
      (item) => item.id === cid
    );
    if (s_cardIndex < 0) return;

    t_boardIndex = boards.findIndex((item) => item.id === targetCard.bid);
    if (t_boardIndex < 0) return;

    t_cardIndex = boards[t_boardIndex]?.cards?.findIndex(
      (item) => item.id === targetCard.cid
    );
    if (t_cardIndex < 0) return;

    const tempBoards = [...boards];
    const sourceCard = tempBoards[s_boardIndex].cards[s_cardIndex];
    tempBoards[s_boardIndex].cards.splice(s_cardIndex, 1);
    tempBoards[t_boardIndex].cards.splice(t_cardIndex, 0, sourceCard);
    setBoards(tempBoards);

    setTargetCard({
      bid: "",
      cid: "",
    });
  };

  const dragEntered = (bid, cid) => {
    if (targetCard.cid === cid) return;
    setTargetCard({
      bid,
      cid,
    });
  };

  const updateCard = (bid, cid, card) => {
    const index = boards.findIndex((item) => item.id === bid);
    if (index < 0) return;

    const tempBoards = [...boards];
    const cards = tempBoards[index].cards;

    const cardIndex = cards.findIndex((item) => item.id === cid);
    if (cardIndex < 0) return;

    tempBoards[index].cards[cardIndex] = card;

    setBoards(tempBoards);
  };


  useEffect(() => {
    localStorage.setItem("prac-kanban", JSON.stringify(boards));
  }, [boards]);

  function abbc(){
    // console.log("fsadfad");
    setSortPriority(true);
  }
  function bbbc(){
    // console.log("dsffsfs");
    setSortPriority(false);
  }
  return (
    <div className="app">
      <div className="app_nav">
        <h1>Kanban Board</h1>
        <button onClick={abbc}> sort by priority </button>
        <button onClick={bbbc}> sort by name </button>
        {/* <button onClick={setSortPriority(false)}> sort by name </button> */}
      </div>
      <div className="app_boards_container">
        <div className="app_boards">
          {boards.map((item) => (
            <Board
              key={item.id}
              board={item}
              addCard={addCardHandler}
              removeBoard={() => removeBoard(item.id)}
              removeCard={removeCard}
              dragEnded={dragEnded}
              dragEntered={dragEntered}
              updateCard={updateCard}
              sortPriority={sortPriority}
            />
          ))}
          <div className="app_boards_last">
            <Editable
              displayClass="app_boards_add-board"
              editClass="app_boards_add-board_edit"
              placeholder="Enter Board Name"
              text="Add Board"
              buttonText="Add Board"
              onSubmit={addboardHandler}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
